<?php/** * Created by PhpStorm. * User: Marjan * Date: 14.04.2016 * Time: 14:21 */namespace Core;use App\Egoose;use Frontend\Mvc\Controller\indexController;class Bootstrap{    public static function Init()    {        if (@Egoose::$URI[0] != null && @Egoose::$URI[0] != "")        {            if (!Controller::Exists(Egoose::$URI[0]))            {                Egoose::SetController("index");                $controller = new indexController();            }            else            {                $controllerType = str_replace("/","",Egoose::$SCOPE) . "\\Mvc\\Controller\\" . Egoose::$URI[0] . 'Controller';                $controller = new $controllerType();            }        }        else        {            Egoose::$URI[0] = "index";            Egoose::SetController("index");            $controller = new indexController();        }        if (@Egoose::$URI[1] != null && @Egoose::$URI[1] != "")        {            $func = Egoose::$URI[1];            if (method_exists($controller, $func))            {                $args = array();                for ($i = 2; $i < count(Egoose::$URI); $i++)                {                    $args[] = Egoose::$URI[$i];                }                call_user_func_array(array($controller, $func), $args);            }            else            {                $args = array();                for ($i = 1; $i < count(Egoose::$URI); $i++)                {                    $args[] = Egoose::$URI[$i];                }                call_user_func_array(array($controller, "index"), $args);            }        }        else        {            $args = array();            for ($i = 0; $i < count(Egoose::$URI); $i++)            {                $args[] = Egoose::$URI[$i];            }            call_user_func_array(array($controller, "index"), $args);        }    }}