<?php/** * Created by PhpStorm. * User: MarjanPopov * Date: 23.12.2016 * Time: 15:10 */namespace Core;class VirtualMethod{    public function __construct()    {    }    /**     * Makes it possible to call Virtual Get and Set methods    */    public function __call($name, $arguments) {        //Get the First 3 Chars to check if Get or Set        $check = substr($name, 0, 3);        //Get Everything after the first 3 chars to get the propertyname        $property_name = substr($name, 3);        $property_name = preg_replace('/\B([A-Z])/', '_$1', $property_name);        $property_name = strtolower($property_name);        if($check=="Get")        {            if(property_exists($this,$property_name))            {                return $this->{$property_name};            }        }        else if($check=="Set")        {            $this->{$property_name} = $arguments[0];            return $this;        }        else        {            //throw method not found exception        }    }}