<?php/* * To change this license header, choose License Headers in Project Properties. * To change this template file, choose Tools | Templates * and open the template in the editor. *//** * Description of Loader * * @author Marjan */namespace Lib\Egoose\AutoLoader;class Loader{    public static function LoadClass($classpath)    {        if(file_exists($_SERVER['DOCUMENT_ROOT'].'/'.$classpath.".php"))            require_once $_SERVER['DOCUMENT_ROOT'].'/'.$classpath.".php";        else            echo $classpath . " Not Found";    }    public static function AutoLoadClass($classname)    {        if (strpos($classname, '_') !== FALSE)        {            $newclassname = str_replace("_", "/", $classname);            if(file_exists($_SERVER['DOCUMENT_ROOT'] . "/" . $classname . ".php"))                require_once $_SERVER['DOCUMENT_ROOT'] . "/" . $classname . ".php";        }    }    public static function AutoLoadController($controllername)    {        if(file_exists($_SERVER['DOCUMENT_ROOT'].'/Egoose'. Egoose::$SCOPE .'/Mvc/Controller/'.$controllername.".php"))            require_once $_SERVER['DOCUMENT_ROOT'].'/Egoose'. Egoose::$SCOPE .'/Mvc/Controller/'.$controllername.".php";    }    public static function AutoLoadModel($modelname)    {        if(file_exists($_SERVER['DOCUMENT_ROOT'].'/Egoose'. Egoose::$SCOPE .'/Mvc/Model/'.$modelname.".php"))            require_once $_SERVER['DOCUMENT_ROOT'].'/Egoose'. Egoose::$SCOPE .'/Mvc/Model/'.$modelname.".php";    }    public static function AutoLoadEntity($entityname)    {        if(file_exists($_SERVER['DOCUMENT_ROOT'].'/Egoose/Entities/'.$entityname.".php"))            require_once $_SERVER['DOCUMENT_ROOT'].'/Egoose/Entities/'.$entityname.".php";    }}