<?php/** * Created by PhpStorm. * User: Marjan * Date: 14.04.2016 * Time: 14:44 */namespace Lib\Egoose\DataBase\Drivers;class Egoose_PDO{    /**@return PDO*/    public static function GetConnection($db_host,$db_name,$db_username, $db_password) : \PDO    {        try        {            $pdo = new \PDO("mysql:host=$db_host;dbname=$db_name", $db_username, $db_password);            $pdo->exec("set names utf8");            return $pdo;        }        catch(PDOException $e)        {            echo $e->getMessage();        }    }}