<?php/** * Created by PhpStorm. * User: Marjan * Date: 25.12.2016 * Time: 08:26 */namespace Lib\Egoose\DataBase\Entity;class EntityFilterParameters{    private $filter_paramters;    /**     * Adds a new Item to $filter_parameters which can be parsed as filters for an Entity::GetCollection or Entity::GetInstance call    */    public function AddFilter(string $key,string $value)    {        if(!empty($this->filter_paramters[$key])) {            $this->filter_paramters[$key] = $value;        }        else        {            new \Exception("This Filter has already been set.");        }    }    public function ToArray()    {        return $this->filter_paramters;    }}