<?php/** * Created by PhpStorm. * User: MarjanPopov * Date: 18.11.2016 * Time: 14:48 */namespace Lib\Egoose\DateTimeManagement;class Dtm{    public static function Now()    {        return date("Y-m-d H:i:s");    }    public static function IsValid($date,$format = 'm.d.Y')    {        $d = \DateTime::createFromFormat($format, $date);        return $d && $d->format($format) == $date;    }}