<?php/** * Created by PhpStorm. * User: Marjan * Date: 23.03.2016 * Time: 14:30 */namespace Lib\Egoose\FileRenderer;use Core\Config\PageConfig;class FileRenderer{    public static function RenderCss()/* : void*/    {        $dir = opendir($_SERVER['DOCUMENT_ROOT'].'/public/templates/'. PageConfig::$TEMPLATE . '/css/');        echo "<!--Auto Rendered StyleSheets-->\n";        while ($file = readdir($dir)) {            if ($file == '.' || $file == '..') {                continue;            }            echo "\t<link type='text/css' rel='stylesheet' href='/public/templates/". PageConfig::$TEMPLATE ."/css/$file'>\n";        }        closedir($dir);    }    public static function RenderHeaderJs() /*:void*/    {        $dir = opendir($_SERVER['DOCUMENT_ROOT'].'/public/templates/'. PageConfig::$TEMPLATE . '/js/header/');        echo "<!--Auto Rendered Header Javascripts-->\n";        while ($file = readdir($dir)) {            if ($file == '.' || $file == '..') {                continue;            }            echo "\t<script src='/public/templates/". PageConfig::$TEMPLATE ."/js/header/$file'></script>\n";        }        closedir($dir);    }    public static function RenderFooterJs() /*:void*/    {        $dir = opendir($_SERVER['DOCUMENT_ROOT'].'/public/templates/'. PageConfig::$TEMPLATE . '/js/footer/');        echo "<!--Auto Rendered Footer Javascripts-->\n";        while ($file = readdir($dir)) {            if ($file == '.' || $file == '..') {                continue;            }            echo "\t<script src='/public/templates/". PageConfig::$TEMPLATE ."/js/footer/$file'></script>\n";        }        closedir($dir);    }    /**BACKEND*/    public static function RenderBackendCss()/* : void*/    {        $dir = opendir($_SERVER['DOCUMENT_ROOT'].'/backend/templates/'. PageConfig::$BACKEND_TEMPLATE . '/css/');        echo "<!--Auto Rendered StyleSheets-->\n";        while ($file = readdir($dir)) {            if ($file == '.' || $file == '..') {                continue;            }            echo "\t<link type='text/css' rel='stylesheet' href='/backend/templates/". PageConfig::$BACKEND_TEMPLATE ."/css/$file'>\n";        }        closedir($dir);    }    public static function RenderBackendHeaderJs() /*:void*/    {        $dir = opendir($_SERVER['DOCUMENT_ROOT'].'/backend/templates/'. PageConfig::$BACKEND_TEMPLATE . '/js/header/');        echo "<!--Auto Rendered Header Javascripts-->\n";        while ($file = readdir($dir)) {            if ($file == '.' || $file == '..') {                continue;            }            echo "\t<script src='/backend/templates/". PageConfig::$BACKEND_TEMPLATE ."/js/header/$file'></script>\n";        }        closedir($dir);    }    public static function RenderBackendFooterJs() /*:void*/    {        $dir = opendir($_SERVER['DOCUMENT_ROOT'].'/backend/templates/'. PageConfig::$BACKEND_TEMPLATE . '/js/footer/');        echo "<!--Auto Rendered Footer Javascripts-->\n";        while ($file = readdir($dir)) {            if ($file == '.' || $file == '..') {                continue;            }            echo "\t<script src='/backend/templates/". PageConfig::$BACKEND_TEMPLATE ."/js/footer/$file'></script>\n";        }        closedir($dir);    }}