<?php/** * Created by PhpStorm. * User: Marjan * Date: 14.04.2016 * Time: 13:06 */namespace Lib\Egoose\Helper;class Helper{    public static function IsNullOrEmptyString($question)    {        return (!isset($question) || trim($question)==='');    }    public static function ValidateEmail($email)    {        if (!filter_var($email, FILTER_VALIDATE_EMAIL) === false)        {            return true;        }        else        {            return false;        }    }}