<?php/** * Created by PhpStorm. * User: MarjanPopov * Date: 17.11.2016 * Time: 16:18 */namespace Lib\Egoose\Http\Ajax;class AjaxResponse extends Entity{    var $success;    var $message;    var $redirect;    var $redirecturl;    var $lastInsertedId;    var $entity;    public function SetSuccess(bool $success)    {        $this->success = $success;        return $this;    }    public function SetEntity(Entity $entity=null)    {        $this->entity = $entity;        return $this;    }    public function SetMessage(string $message)    {        $this->message = $message;        return $this;    }    public function SetRedirect(bool $redirect)    {        $this->redirect = $redirect;        return $this;    }    public function SetRedirectUrl(string $redirecturl)    {        $this->redirecturl = $redirecturl;        return $this;    }    public function SetLastInsertedId(int $lastinsertedId)    {        $this->lastInsertedId = $lastinsertedId;        return $this;    }    public function Send()    {        session_cache_limiter('nocache');        header('Expires: ' . gmdate('r', 0));        header('Content-type: application/json');        echo json_encode($this);        exit();    }}