<?php/** * Created by PhpStorm. * User: MarjanPopov * Date: 24.11.2016 * Time: 15:39 */class Required{    private $fulfilled=true;    private $required_value;    private $controller;    public function __construct($required_value,Controller $controller)    {        $this->controller = $controller;        $this->required_value = $required_value;        if($this->required_value==null)            $this->fulfilled=false;        return $this;    }    public function NotEmpty()    {        if((!isset($this->required_value) || trim($this->required_value)===''))            $this->fulfilled=false;        return $this;    }    public function Redirect(string $path)    {        if(!$this->fulfilled)            $this->controller->Redirect($path);    }    public function ShowMessage()    {        if(!$this->fulfilled)            $this->controller->SetMessage();    }}