<?php/** * Created by PhpStorm. * User: MarjanPopov * Date: 24.11.2016 * Time: 16:00 */namespace Lib\Egoose\Output\Message;class Message{    private $message_type;    private $message;    public function __construct(string $message_type, string $message)    {        $this->message_type = $message_type;        $this->message = $message;    }    public function GetType()    {        return $this->message_type;    }    public function GetMessage()    {        return $this->message;    }}