<?php/** * Created by PhpStorm. * User: MarjanPopov * Date: 23.12.2016 * Time: 13:44 */namespace Lib\Egoose\Scope\Backend;class Backend{    private $userManager;    private $loginManager;    function __construct()    {        $this->loginManager                 = new LoginManager();        $this->userManager                  = new UserManager($this->loginManager);    }    /**@return Navigation*/    public function Navigation() : Navigation    {        return Navigation::GetInstance();    }    /**@return TopBar*/    public function TopBar() : TopBar    {        return TopBar::GetInstance();    }    public function UserManager()    {        return $this->userManager;    }    public function LoginManager()    {        return $this->loginManager;    }}