<?php/** * Created by PhpStorm. * User: MarjanPopov * Date: 23.12.2016 * Time: 13:52 */namespace Lib\Egoose\Scope\Backend;use Core\Entity;use Entities;class Navigation{    protected static $instance;    private $active_navi            = "dashboard";    public static function GetInstance()    {        if(null == self::$instance)            self::$instance = new self;        return self::$instance;    }    public function SetActiveNavi(string $active_navi)    {        $this->active_navi = $active_navi;    }    public function GetActiveNavi() : string    {        return $this->active_navi;    }    public function GetNavigationBullets()    {        return Entity::GetCollection("backend_navigation_item");    }    public function AddPermanendNavigationItem(Entities\backend_navigation_item $backend_navigation_item)    {        $backend_navigation_item->Save();    }}