<?php/** * Created by PhpStorm. * User: MarjanPopov * Date: 23.12.2016 * Time: 13:44 */namespace Lib\Egoose\Scope\Frontend;use Core\Entity;use Entities\frontend_menu;use Lib\Egoose\UserManagement\Frontend\LoginManager;use Lib\Egoose\UserManagement\Frontend\UserManager;class Frontend{    private $userManager;    private $loginManager;    /**     * @var $name string     * @return frontend_menu     */    function __construct()    {        $this->loginManager                 = new LoginManager();        $this->userManager                  = new UserManager($this->loginManager);    }    public function GetMenu(string $name)    {        return Entity::GetInstance("frontend_menu")->GetBy("name",$name);    }    public function UserManager()    {        return $this->userManager;    }    public function LoginManager()    {        return $this->loginManager;    }}