<?php/** * Created by PhpStorm. * User: MarjanPopov * Date: 01.12.2016 * Time: 16:24 */namespace Lib\Egoose\UserManagement\Backend;use Lib\Egoose\UserManagement\AbstractLoginManager;use Lib\Egoose\Http\Session\Session;class LoginManager extends AbstractLoginManager{    public function IsLoggedIn()    {        if(Session::Exists("backend_login"))        {            if(Session::Get("backend_login"))                return true;            return false;        }        else        {            return false;        }    }    public function Login(\Entities\backend_user $backend_user)    {        Session::Set("backend_login",true);        Session::Set("backend_user_id",$backend_user->GetId());    }    public function Logout()    {        Session::Remove("backend_user_id");        Session::Set("backend_login",false);    }}