<?php/** * Created by PhpStorm. * User: MarjanPopov * Date: 01.12.2016 * Time: 16:22 */namespace Lib\Egoose\UserManagement\Backend;class UserManager{    private $loginManager;    public function __construct(LoginManager $loginManager)    {        $this->loginManager = $loginManager;    }    public function GetUser()    {        if($this->loginManager->IsLoggedIn())            return Entity::GetInstance("backend_user")->Get(Session::Get("backend_user_id"));        return null;    }}