<?php/** * Created by PhpStorm. * User: MarjanPopov * Date: 01.12.2016 * Time: 16:24 */namespace Lib\Egoose\UserManagement\Frontend;use Entities\frontend_user;use Lib\Egoose\Http\Session\Session;use Lib\Egoose\UserManagement\AbstractLoginManager;class LoginManager extends AbstractLoginManager{    public function IsLoggedIn()    {        if(Session::Exists("frontend_login"))        {            if(Session::Get("frontend_login"))                return true;            return false;        }        else        {            return false;        }    }    public function Login(frontend_user $frontend_user)    {        Session::Set("frontend_login",true);        Session::Set("frontend_user_id",$frontend_user->GetId());    }    public function Logout()    {        Session::Remove("frontend_user_id");        Session::Set("frontend_login",false);    }}