<?php/** * Created by PhpStorm. * User: MarjanPopov * Date: 01.12.2016 * Time: 16:22 */namespace Lib\Egoose\UserManagement\Frontend;use Core\Entity;use Entities\frontend_user_type;use Lib\Egoose\Http\Session\Session;class UserManager{    private $loginManager;    public function __construct(LoginManager $loginManager)    {        $this->loginManager = $loginManager;    }    public function GetUser()    {        if($this->loginManager->IsLoggedIn())        {            if($this->GetFrontendUserType() == frontend_user_type::$BASIC)                return Entity::GetInstance("frontend_user")->Get(Session::Get("frontend_user_id"));            else            {                return Entity::GetInstance("frontend_user_" . $this->GetFrontendUserType())->Get(Session::Get("frontend_user_id"));            }        }        return null;    }    public function GetFrontendUserType() : ?string    {        return Session::Get("frontend_user_type");    }    public function SetFrontendUserType(string $frontend_user_type) : void    {        Session::Set("frontend_user_type",$frontend_user_type);    }}